import logo from "../img/example.png";
import { useState, } from "react";
import { useCookies } from "react-cookie";
import AuthModal from "../components/AuthModal";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";


const Nav = ({ authToken, setShowModal, showModal, setIsSignUp }) => {
  const [cookies, setCookie, removeCookie] = useCookies(['user']);
  const handleSignOut = () => {
    if (authToken) {
      removeCookie('UserId', cookies.UserId);
      removeCookie('AuthToken', cookies.AuthToken);
      window.location.reload();
      <Link to="/"></Link>

      return
    }
    setShowModal(true);
    setIsSignUp(true);
  }
  const handleSignIn = () => {
    setShowModal(true)
    setIsSignUp(false)
  }

  return (
    <nav>
      <a class="logo-container" href="http://localhost:3000/">
        <img className="logo" src={logo} />
      </a>
      {!authToken ? <button className="nav-button" onClick={handleSignIn} disabled={showModal}>
        Log in</button> : <button className="nav-button" onClick={handleSignOut}>Sign Out</button>}
    </nav>
  )
}
export default Nav
