import Nav from '../components/Nav';
import { useEffect, useState } from 'react';
import { useCookies } from 'react-cookie';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const OnBoarding = () => {
    const [showCarList, setShowCarList] = useState(false)
    const [postCar, setPostCar] = useState(false)
    const [discover, setDiscover] = useState(false)
    const listClick = () => {
        setShowCarList(true)
        setPostCar(false)
        setDiscover(false)
    }
    const postClick = () => {
        setShowCarList(false)
        setPostCar(true)
        setDiscover(false)
    }
    const discoverClick = () => {
        setShowCarList(false)
        setPostCar(false)
        setDiscover(true)
    }

    const [cookies, setCookie, removeCookie] = useCookies(null)
    const [formData, setFormData] = useState({
        model: '',
        url: '',
        year: '',
        price: ''
    })
    const [cars, setCars] = useState([]);

    useEffect(() => {
        getCarInfo();
    }, [])

    let navigate = useNavigate();

    const getCarInfo = async () => {
        const user_id = cookies.UserId;
        try {
            const response = await axios.get('http://localhost:8000/cars', { params: { user_id } });
            console.log('response.data', response.data);
            setCars([...response.data.cars]);
        } catch (err) {
            console.log(err);
        }
    }
    const deleteACar = async (id) => {
        const user_id = cookies.UserId;
        try {
            const carArray = [...cars];
            const response = await axios.delete('http://localhost:8000/cars', { params: { id, user_id } });
            carArray.splice(id, 1);
            console.log('carid', id);
            console.log('carArray', carArray);
            setCars([...carArray]);
        } catch (err) {
            console.log(err);
        }
    }

    const handleSubmit = async (e) => {
        console.log('submitted');
        const user_id = cookies.UserId;
        const id = cars.length.toString();
        console.log('id', id);
        e.preventDefault();
        try {
            const response = await axios.put('http://localhost:8000/cars', { formData, user_id, id });
            console.log(response);
            const success = response.status === 200;
            const newCar = { ...formData, id };
            console.log('newCar', newCar);
            setCars((prevState) => [...prevState, newCar]);
        } catch (err) {
            console.log(err);
        }

    }

    const handleChange = (e) => {
        console.log('e', e)
        const value = e.target.type === "checkbox" ? e.target.checked : e.target.value
        const name = e.target.name

        setFormData((prevState) => ({
            ...prevState,
            [name]: value
        }))
    }

    return (
        <>
            <Nav
                minimal={true}
                setShowModal={() => {
                }}
                showModal={false}
            />
            <div className="individual">
                <button className="primary-button" onClick={listClick}>Car List</button>
                <button className="primary-button" onClick={postClick}>Post a New Car</button>
                <button className="primary-button" onClick={discoverClick}>Discover</button>
                {showCarList && <div className="car-container">
                    <Car />
                </div>}
                {postCar && <div className="post-car">
                    <OnBoarding />
                </div>}
                {discover && <div className="discover">
                    <Dashboard />
                </div>}
                {!showCarList && !postCar && !discover && <div className="welcome-page">
                    <h1>Welcome</h1>
                </div>
                }
            </div>






            <div className="onboarding">
                <h2>UPLOAD CARS</h2>

                <form onSubmit={handleSubmit}>
                    <section>
                        <label htmlFor="Model">Model</label>
                        <input
                            id="Model"
                            type='text'
                            name="model"
                            placeholder="Model"
                            required={true}
                            value={formData.model}
                            onChange={handleChange}
                        />

                        <label htmlFor="Price">Price</label>
                        <input
                            id="Price"
                            type='text'
                            name="price"
                            placeholder="Price"
                            required={true}
                            value={formData.price}
                            onChange={handleChange}
                        />


                        <label htmlFor="Year">Year</label>
                        <input
                            id="Year"
                            type="text"
                            name="year"
                            required={true}
                            placeholder="Year"
                            value={formData.year}
                            onChange={handleChange}
                        />

                        <input type="submit" />
                    </section>

                    <section>

                        <label htmlFor="url">Photo</label>
                        <input
                            type="url"
                            name="url"
                            id="url"
                            onChange={handleChange}
                            required={true}
                        />
                        <div className="photo-container">
                            {formData.url && <img src={formData.url} alt="profile pic preview" />}
                        </div>
                    </section>
                </form>
            </div>
            <h3>Car Posted</h3>
            <div className="card-container">
                {cars.map((car) =>
                    <div key={car.id}>
                        <div style={{ backgroundImage: 'url(' + car.url + ')' }} className='card'>
                        </div>
                        <h3> {car.model}({car.year}) </h3>
                        <h3>${car.price}</h3>
                        <button type="submit" onClick={() => deleteACar(car.id)} >Delete</button>
                    </div>)}
            </div>
        </>
    )
}
export default OnBoarding
